import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AccountContext } from "./User_Account.jsx";
import { Auth } from "aws-amplify";
import axios from "axios";

const getUserInfo = async (accessToken, setAuthenticated, navigate) => {
  try {
    const { data } = await axios.post("/getFacebookData", {
      page_id: "YOUR_PAGE_ID",
      access_token: accessToken,
    });

    console.log("Facebook data:", data);

    // Process the retrieved Facebook data as needed

    // Redirect the user to the dashboard or perform any desired actions
    setAuthenticated(true);
    navigate("/User_dashboard");
  } catch (error) {
    console.error("Failed to retrieve Facebook data:", error);
  }
};

const User_login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();
  const { authenticate, setAuthenticated } = useContext(AccountContext);

  useEffect(() => {
    window.fbAsyncInit = function () {
      window.FB.init({
        appId: "730655258288890",
        cookie: true,
        xfbml: true,
        version: "v15.0",
      });

      window.FB.AppEvents.logPageView();

      window.FB.getLoginStatus(function (response) {
        statusChangeCallback(response);
      });
    };

    (function (d, s, id) {
      var js,
        fjs = d.getElementsByTagName(s)[0];
      if (d.getElementById(id)) return;
      js = d.createElement(s);
      js.id = id;
      js.src = "https://connect.facebook.net/en_US/sdk.js";
      fjs.parentNode.insertBefore(js, fjs);
    })(document, "script", "facebook-jssdk");
  }, []);

  const statusChangeCallback = (response) => {
    console.log("Status Change Callback Response:", response);
    if (response.status === "connected") {
      getUserInfo(
        response.authResponse.accessToken,
        setAuthenticated,
        navigate
      );
    } else {
      console.log("User cancelled login or did not fully authorize.");
      document.getElementById("status").innerHTML =
        "Please log into this webpage.";
    }
  };

  const checkLoginState = () => {
    console.log("Checking login state");
    window.FB.getLoginStatus(function (response) {
      console.log("Got login status response", response);
      statusChangeCallback(response);
    });
  };

  const onSubmit = (event) => {
    event.preventDefault();

    authenticate(email, password)
      .then((data) => {
        setAuthenticated(true);
        navigate("/User_dashboard");
      })
      .catch((err) => {
        console.log("Failed to login", err || "No error message provided 1");
      });
  };

  return (
    <div>
      <form onSubmit={onSubmit}>
        <label htmlFor="email">Email</label>
        <input
          value={email}
          onChange={(event) => setEmail(event.target.value)}
        ></input>
        <label htmlFor="password">Password</label>
        <input
          value={password}
          onChange={(event) => setPassword(event.target.value)}
        ></input>
        <button type="submit">Login</button>
      </form>
      <div
        className="fb-login-button"
        data-width=""
        data-size="large"
        data-button-type="continue_with"
        data-layout="default"
        data-auto-logout-link="false"
        data-use-continue-as="true"
      ></div>
      <button onClick={checkLoginState}> Hi </button>
    </div>
  );
};

export default User_login;
