import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AccountContext } from "./User_Account.jsx";
import { Auth } from "aws-amplify";

const User_login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [fbLoaded, setFbLoaded] = useState(false); // New state variable
  const navigate = useNavigate();
  const { authenticate, setAuthenticated } = useContext(AccountContext);

  const getUserInfo = () => {
    console.log("Getting user info");
    window.FB.api(
      "/me",
      {
        fields: "first_name,last_name,email",
        appsecret_proof: process.env.PASS_PHRASE,
      },
      function (response) {
        console.log("User info response:", response); // Added logging here
        console.log("Successful login for: " + response.name);

        const authResponse = window.FB.getAuthResponse();
        let { accessToken, expiresIn } = authResponse;

        if (!accessToken || !expiresIn) {
          console.log("Access token or expiration time is missing.");
          return;
        }

        document.getElementById("status").innerHTML =
          "Thanks for logging in, " + response.name + "!";

        console.log("Facebook Access Token: ", accessToken);

        const { email, first_name, last_name, gender } = response;

        console.log(
          "FACEBOOK_APP_SECRET: ",
          process.env.REACT_APP_FACEBOOK_APP_SECRET
        );

        window.FB.api(
          "/oauth/access_token",
          {
            client_id: "730655258288890",
            client_secret: process.env.REACT_APP_FACEBOOK_APP_SECRET,
            grant_type: "fb_exchange_token",
            fb_exchange_token: accessToken,
          },
          function (res) {
            if (!res || res.error) {
              console.log(
                "Failed to extend access token lifespan: ",
                res.error
              );
            } else {
              accessToken = res.access_token;
              expiresIn = res.expires_in; // Modified line

              const currentTimestamp = Date.now();
              const computedExpiresAt = expiresIn * 1000 + currentTimestamp;

              if (!accessToken || !computedExpiresAt) {
                console.log(
                  "Access token or computed expires time is missing."
                );
                return;
              }

              Auth.federatedSignIn(
                "facebook",
                {
                  token: accessToken,
                  expires_at: computedExpiresAt,
                },
                {
                  given_name: first_name,
                  family_name: last_name,
                  email: email,
                  gender: gender,
                  picture: "",
                  email_verified: true,
                }
              )
                .then((data) => {
                  console.log("Successful federated sign-in:", data);
                  setAuthenticated(true);
                  navigate("/User_dashboard");
                })
                .catch((err) => {
                  console.log(
                    "Failed to login",
                    err || "No error message provided"
                  );
                });
            }
          }
        );
      }
    );
  };

  const statusChangeCallback = (response) => {
    console.log("Status Change Callback Response:", response);
    if (response.status === "connected") {
      getUserInfo();
    } else {
      console.log("User cancelled login or did not fully authorize.");
      document.getElementById("status").innerHTML =
        "Please log into this webpage.";
    }
  };

  useEffect(() => {
    const getUserInfo = () => {
      console.log("Getting user info");
      window.FB.api(
        "/me",
        {
          fields: "first_name,last_name,email",
          appsecret_proof: process.env.PASS_PHRASE,
        },
        function (response) {
          console.log("User info response:", response); // Added logging here
          console.log("Successful login for: " + response.name);

          const authResponse = window.FB.getAuthResponse();
          let { accessToken, expiresIn } = authResponse;

          if (!accessToken || !expiresIn) {
            console.log("Access token or expiration time is missing.");
            return;
          }

          document.getElementById("status").innerHTML =
            "Thanks for logging in, " + response.name + "!";

          console.log("Facebook Access Token: ", accessToken);

          const { email, first_name, last_name, gender } = response;

          console.log(
            "FACEBOOK_APP_SECRET: ",
            process.env.REACT_APP_FACEBOOK_APP_SECRET
          );

          window.FB.api(
            "/oauth/access_token",
            {
              client_id: "730655258288890",
              client_secret: process.env.REACT_APP_FACEBOOK_APP_SECRET,
              grant_type: "fb_exchange_token",
              fb_exchange_token: accessToken,
            },
            function (res) {
              if (!res || res.error) {
                console.log(
                  "Failed to extend access token lifespan: ",
                  res.error
                );
              } else {
                accessToken = res.access_token;
                expiresIn = res.expires_in; // Modified line

                const currentTimestamp = Date.now();
                const computedExpiresAt = expiresIn * 1000 + currentTimestamp;

                if (!accessToken || !computedExpiresAt) {
                  console.log(
                    "Access token or computed expires time is missing."
                  );
                  return;
                }

                Auth.federatedSignIn(
                  "facebook",
                  {
                    token: accessToken,
                    expires_at: computedExpiresAt,
                  },
                  {
                    given_name: first_name,
                    family_name: last_name,
                    email: email,
                    gender: gender,
                    picture: "",
                    email_verified: true,
                  }
                )
                  .then((data) => {
                    console.log("Successful federated sign-in:", data);
                    setAuthenticated(true);
                    navigate("/User_dashboard");
                  })
                  .catch((err) => {
                    console.log(
                      "Failed to login",
                      err || "No error message provided"
                    );
                  });
              }
            }
          );
        }
      );
    };

    const statusChangeCallback = (response) => {
      console.log("Status Change Callback Response:", response);
      if (response.status === "connected") {
        getUserInfo();
      } else {
        console.log("User cancelled login or did not fully authorize.");
        document.getElementById("status").innerHTML =
          "Please log into this webpage.";
      }
    };

    if (window.FB) {
      // if FB SDK is already loaded, we just need to set the statusChangeCallback
      window.FB.getLoginStatus(function (response) {
        statusChangeCallback(response);
      });
    } else {
      window.fbAsyncInit = function () {
        window.FB.init({
          appId: "730655258288890",
          cookie: true,
          xfbml: true,
          version: "v15.0",
        });

        window.FB.AppEvents.logPageView();

        window.FB.getLoginStatus(function (response) {
          statusChangeCallback(response);
        });
      };

      (function (d, s, id) {
        var js,
          fjs = d.getElementsByTagName(s)[0];
        if (d.getElementById(id)) return;
        js = d.createElement(s);
        js.id = id;
        js.src = "https://connect.facebook.net/en_US/sdk.js";
        fjs.parentNode.insertBefore(js, fjs);
      })(document, "script", "facebook-jssdk");
    }
  }, []);

  const checkLoginState = () => {
    console.log("Checking login state"); // Added logging here
    window.FB.getLoginStatus(function (response) {
      console.log("Got login status response", response); // Added logging here
      statusChangeCallback(response);
    });
  };

  const onSubmit = (event) => {
    event.preventDefault();

    authenticate(email, password)
      .then((data) => {
        setAuthenticated(true);
        navigate("/User_dashboard");
      })
      .catch((err) => {
        console.log("Failed to login", err || "No error message provided 1");
      });
  };

  return (
    <div>
      <form onSubmit={onSubmit}>
        <label htmlFor="email">Email</label>
        <input
          value={email}
          onChange={(event) => setEmail(event.target.value)}
        ></input>
        <label htmlFor="password">Password</label>
        <input
          value={password}
          onChange={(event) => setPassword(event.target.value)}
        ></input>
        <button type="submit">Login</button>
      </form>
      <div
        className="fb-login-button"
        data-width=""
        data-size="large"
        data-button-type="continue_with"
        data-layout="default"
        data-auto-logout-link="false"
        data-use-continue-as="true"
      ></div>
      <button onClick={checkLoginState}> Hi </button>
    </div>
  );
};

export default User_login;
