import React, { useEffect, useState } from "react";
import UserPool from "../User_Userpool.jsx"; // adjust the import path to where your User_Userpool.jsx file is located
import { Auth } from "aws-amplify";
import { useHistory } from "react-router-dom";

const User_dashboard = () => {
  const [givenName, setGivenName] = useState("");
  const history = useHistory(); // using useHistory hook for redirecting

  useEffect(() => {
    const cognitoUser = UserPool.getCurrentUser();

    if (cognitoUser != null) {
      cognitoUser.getSession((err, session) => {
        if (err) {
          alert(err);
          return;
        }

        console.log("session validity: " + session.isValid());
        setGivenName(session.getIdToken().payload["given_name"]);
      });
    }
  }, []);

  // New logout function
  const handleLogout = async () => {
    try {
      await Auth.signOut(); // This clears the user's session
      history.push("/User_login"); // Redirect the user to the login page
    } catch (error) {
      console.error("Error signing out user", error);
    }
  };

  return (
    <div>
      Hi, {givenName}
      <button onClick={handleLogout}>Logout</button>{" "}
      {/* Button to trigger logout */}
    </div>
  );
};

export default User_dashboard;
