import React, { useState } from "react";
import Dropzone from "react-dropzone";
import { S3Client, PutObjectCommand } from "@aws-sdk/client-s3";
import { BsCloudUpload } from "react-icons/bs";

// Configure the AWS SDK
const s3 = new S3Client({
  region: "us-east-1", // Replace with your desired region
  credentials: {
    accessKeyId: "AKIA2AJRN6P67CIV6TTD",
    secretAccessKey: "ZA/r6XSUs2GdDq5Io3ryjJolRvp31WMvIMKj9pRp",
  },
});

const ImageUpload = () => {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [cost, setCost] = useState("");
  const [address, setAddress] = useState("");
  const [email, setEmail] = useState("");

  const handleFileDrop = (acceptedFiles) => {
    setSelectedFiles([...selectedFiles, ...acceptedFiles]);
  };

  const handleRemoveFile = (index) => {
    const newSelectedFiles = [...selectedFiles];
    newSelectedFiles.splice(index, 1);
    setSelectedFiles(newSelectedFiles);
  };

  const handleUpload = async () => {
    if (selectedFiles.length > 0) {
      const uploadPromises = selectedFiles.map((file) => {
        const params = {
          Bucket: "pilli-storage-f68b2ca801743-dev",
          Key: file.name,
          Body: file,
          ACL: "public-read",
        };
        return s3.send(new PutObjectCommand(params));
      });

      try {
        const results = await Promise.all(uploadPromises);
        console.log("Images uploaded successfully:", results);
        // Perform any further actions after successful image upload
      } catch (err) {
        console.log("Error:", err);
      }
    }
  };

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100vh",
      }}
    >
      <div>
        <Dropzone onDrop={handleFileDrop}>
          {({ getRootProps, getInputProps }) => (
            <div {...getRootProps()}>
              <input {...getInputProps()} />
              {selectedFiles.length > 0 ? (
                <div>
                  {selectedFiles.map((file, index) => (
                    <div key={index}>
                      <span>{file.name}</span>
                      <button onClick={() => handleRemoveFile(index)}>
                        Remove
                      </button>
                    </div>
                  ))}
                </div>
              ) : (
                <div>
                  <h1>Create a new listing</h1>
                  <BsCloudUpload size={48} />
                  <div>Drag and drop images here or click to select</div>
                </div>
              )}
            </div>
          )}
        </Dropzone>
        <form>
          <label>
            Cost:
            <input
              type="text"
              value={cost}
              onChange={(e) => setCost(e.target.value)}
            />
          </label>
          <label>
            Address:
            <input
              type="text"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
            />
          </label>
          <label>
            Email:
            <input
              type="text"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </label>
        </form>
        <button onClick={handleUpload}>Upload</button>
      </div>
    </div>
  );
};

export default ImageUpload;
