import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { Auth } from "aws-amplify";
import { AccountContext } from "./User_Account.jsx";
import FacebookLogin from "facebook-login";

const User_login = () => {
  const [email, setEmail] = useState("");
  const [fbLoaded, setFbLoaded] = useState(false);
  const navigate = useNavigate();
  const { authenticate, setAuthenticated } = useContext(AccountContext);

  const getUserInfo = async () => {
    const response = await window.FB.api("/me", {
      fields: "name,email", // Changed fields to name,email
      appsecret_proof: process.env.PASS_PHRASE,
    });

    console.log("User info response:", response);
    console.log("Successful login for: " + response.name);

    const authResponse = window.FB.getAuthResponse();
    let { accessToken, expiresIn } = authResponse;

    if (!accessToken || !expiresIn) {
      console.log("Access token or expiration time is missing.");
      return;
    }

    document.getElementById("status").innerHTML =
      "Thanks for logging in, " + response.name + "!";

    console.log("Facebook Access Token: ", accessToken);

    const { email, name } = response; // Changed public_profile to name

    // Update the user's email and name attributes
    Auth.updateUserAttributes(authenticate().user, {
      email,
      name, // Changed public_profile to name
    });

    // Redirect the user to the dashboard
    navigate("/User_dashboard");
  };

  const checkLoginState = () => {
    console.log("Checking login state");
    if (fbLoaded && window.FB.getAuthResponse().status === "connected") {
      getUserInfo();
    }
  };

  useEffect(() => {
    if (window.FB) {
      window.FB.init({
        appId: "730655258288890",
        cookie: true,
        xfbml: true,
        version: "v15.0",
      });

      window.FB.getLoginStatus((response) => {
        if (response.status === "connected") {
          getUserInfo();
        } else {
          setFbLoaded(true);
        }
      });
    } else {
      console.log("Facebook SDK not loaded yet");
    }
  }, []);

  const onSubmit = (event) => {
    event.preventDefault();

    // Authenticate the user with the Amplify Auth service
    const password = prompt("Please enter your password:");
    Auth.signIn(email, password, (err, data) => {
      if (err) {
        console.log("Error signing in user:", err);
      } else {
        console.log("Successfully signed in user:", data);

        // Check the user's login state
        checkLoginState();

        // Add the user to the AWS user pool
        Auth.adminCreateUser(email, password, (err, data) => {
          if (err) {
            console.log("Error creating user:", err);
          } else {
            console.log("Successfully created user:", data);

            // Update the user's attributes
            Auth.updateUserAttributes(data.userAttributes.sub, {
              email: response.email,
              family_name: response.name.family_name,
              gender: response.name.gender,
              given_name: response.name.given_name,
              // public_profile: false,
            });
          }
        });
      }
    });
  };

  return (
    <div>
      <form onSubmit={onSubmit}>
        <label htmlFor="email">Email</label>
        <input
          value={email}
          onChange={(event) => setEmail(event.target.value)}
        ></input>
        <button type="submit">Login</button>
      </form>
      <div
        className="fb-login-button"
        data-width=""
        data-size="large"
        data-button-type="continue_with"
        data-layout="default"
        data-auto-logout-link="false"
        data-use-continue-as="true"
      ></div>
      <button onClick={checkLoginState} disabled={!fbLoaded}>
        Hi
      </button>
    </div>
  );
};

export default User_login;
