import React, { useContext } from "react";
import "./App.css";
import Signup from "./components/Signup.jsx";
import Login from "./components/Login.jsx";
import Account, { AccountContext } from "./components/Account.jsx";
import Status from "./components/Status.jsx";
import Dashboard from "./components/Dashboard.jsx";
import { Route } from "react-router-dom"; // import Route component
const App = () => {
  const { authenticated } = useContext(AccountContext);

  return (
    <Account>
      <Status />
      <Signup />
      <Login />
      {authenticated && (
        <Route path="/Dashboard.jsx" component={Dashboard} />
      )}{" "}
    </Account>
  );
};

export default App;
