// User_login.jsx

import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AccountContext } from "./User_Account.jsx";
import { Auth } from "aws-amplify";

const User_login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();
  const { authenticate, setAuthenticated } = useContext(AccountContext);

  useEffect(() => {
    // Load the Facebook SDK
    window.fbAsyncInit = function () {
      window.FB.init({
        appId: "730655258288890", // Replace with your Facebook App ID
        cookie: true,
        xfbml: true,
        version: "v15.0", // Replace with your Facebook SDK version
      });

      window.FB.AppEvents.logPageView();

      window.FB.getLoginStatus(function (response) {
        statusChangeCallback(response);
      });
    };

    // Load the Facebook SDK asynchronously
    (function (d, s, id) {
      var js,
        fjs = d.getElementsByTagName(s)[0];
      if (d.getElementById(id)) return;
      js = d.createElement(s);
      js.id = id;
      js.src = "https://connect.facebook.net/en_US/sdk.js";
      fjs.parentNode.insertBefore(js, fjs);
    })(document, "script", "facebook-jssdk");
  }, []);

  const statusChangeCallback = (response) => {
    if (response.status === "connected") {
      getUserInfo();
    }
  };

  const getUserInfo = () => {
    window.FB.api(
      "/me",
      { fields: "email,first_name,last_name,gender" },
      function (response) {
        const { accessToken, expiresIn } = window.FB.getAuthResponse();
        const { email, first_name, last_name, gender } = response;

        console.log("First Name: ", first_name); // prints first name
        console.log("Last Name: ", last_name); // prints last name
        console.log("Email: ", email); // prints email
        console.log("Gender: ", gender); // prints gender

        Auth.federatedSignIn(
          "facebook",
          {
            token: accessToken,
            expires_at: expiresIn * 1000 + new Date().getTime(),
          },
          {
            name: `${first_name} ${last_name}`,
            email: email,
            picture: "", // Add the user's picture here
            email_verified: true, // set this to true or false as per your user's email verification status
          }
        )
          .then((data) => {
            console.log("Logged", data);
            setAuthenticated(true);
            navigate("/User_dashboard");
          })
          .catch((err) => {
            console.log("Failed to login", err);
          });
      }
    );
  };

  const onFacebookLogin = () => {
    window.FB.login(
      function (response) {
        if (response.authResponse) {
          getUserInfo();
        } else {
          console.log("User cancelled login or did not fully authorize.");
        }
      },
      { scope: "email" }
    );
  };

  const onSubmit = (event) => {
    event.preventDefault();

    authenticate(email, password)
      .then((data) => {
        console.log("Logged", data);
        setAuthenticated(true);
        navigate("/User_dashboard");
      })
      .catch((err) => {
        console.log("Failed to login", err);
      });
  };

  return (
    <div>
      <form onSubmit={onSubmit}>
        <label htmlFor="email">Email</label>
        <input
          value={email}
          onChange={(event) => setEmail(event.target.value)}
        ></input>
        <label htmlFor="password">Password</label>
        <input
          value={password}
          onChange={(event) => setPassword(event.target.value)}
        ></input>
        <button type="submit">Login</button>
      </form>
      <div
        className="fb-login-button"
        data-width=""
        data-size="large"
        data-button-type="continue_with"
        data-layout="default"
        data-auto-logout-link="false"
        data-use-continue-as="true"
        onClick={onFacebookLogin}
      ></div>
    </div>
  );
};

export default User_login;
