import React, { useState, useEffect } from "react";
import { LoginSocialFacebook } from "reactjs-social-login";
import { FacebookLoginButton } from "react-social-login-buttons";
import { ErrorBoundary } from "react-error-boundary";

function FacebookLogin() {
  const [profile, setProfile] = useState(null);
  const [isPolling, setIsPolling] = useState(false);

  const poll = async (accessToken) => {
    setIsPolling(true);
    try {
      const response = await fetch(
        `https://graph.facebook.com/me?fields=name&access_token=${accessToken}`
      );
      const data = await response.json();
      setProfile(data);
      setIsPolling(false);
    } catch (error) {
      console.error(error);
      setIsPolling(false); // Set isPolling to false in case of an error
    }
  };

  const handleFacebookLogin = (response) => {
    console.log("Complete response: ", response);
    if (response && response.data) {
      poll(response.data.access_token);
    } else {
      console.error("Response or response.data is undefined");
    }
  };

  return (
    <div>
      <h1>Facebook Login</h1>
      <LoginSocialFacebook
        appId="730655258288890"
        onResolve={handleFacebookLogin}
        onReject={(error) => {
          console.log(error);
        }}
      >
        <FacebookLoginButton onClick={poll} />
      </LoginSocialFacebook>
      {profile && (
        <div>
          <h1>{profile.name}</h1>
        </div>
      )}
      {/* Add console log for the complete profile object */}
      {console.log("Complete profile: ", profile)}
      {isPolling ? (
        <div>Polling...</div>
      ) : (
        <div>
          <button onClick={poll}>Poll</button>
        </div>
      )}
    </div>
  );
}

export default FacebookLogin;
