import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { Auth } from "aws-amplify";
import { AccountContext } from "./User_Account.jsx";

const User_login = () => {
  const [email, setEmail] = useState("");
  const navigate = useNavigate();
  const { authenticate, setAuthenticated } = useContext(AccountContext);
  const [givenName, setGivenName] = useState("");

  window.statusChangeCallback = (response) => {
    console.log("statusChangeCallback");
    console.log(response);
    if (response.status === "connected") {
      window.getUserInfo();
    } else {
      document.getElementById("status").innerHTML = "Please log into this app.";
    }
  };

  function testAPI() {
    console.log("Welcome!  Fetching your information.... ");
    FB.api("/me", function (response) {
      console.log("Successful login for: " + response.name);
      document.getElementById("status").innerHTML =
        "Thanks for logging in, " + response.name + "!";

      fbUser = response.id;
    });
  }

  function statusChangeCallback(response) {
    console.log("statusChangeCallback");
    console.log(response);
    if (response.status === "connected") {
      // Logged into your app and Facebook.
      testAPI();
    } else if (response.status === "not_authorized") {
      // The person is logged into Facebook, but not your app.
      document.getElementById("status").innerHTML =
        "Please log " + "into this app.";
    } else {
      // The person is not logged into Facebook, so we're not sure if
      // they are logged into this app or not.
      document.getElementById("status").innerHTML =
        "Please log " + "into Facebook.";
    }
  }

  window.getUserInfo = () => {
    console.log("Getting user info");
    window.FB.api(
      "/me",
      {
        fields: "name,email",
        appsecret_proof: process.env.PASS_PHRASE,
      },
      (response) => {
        console.log("User info response:", response);

        const { email, name } = response;

        Auth.updateUserAttributes(authenticate().user, {
          email,
          name,
        });

        navigate("/User_dashboard");
      }
    );
  };

  useEffect(() => {
    if (window.FB) {
      window.FB.init({
        appId: "730655258288890",
        cookie: true,
        xfbml: true,
        version: "v15.0",
      });

      window.FB.getLoginStatus((response) => {
        window.statusChangeCallback(response);
      });

      // Add a listener to the auth.statusChange event
      window.FB.Event.subscribe(
        "auth.statusChange",
        window.statusChangeCallback
      );

      // Add a listener to the auth.logout event
      window.FB.Event.subscribe("auth.logout", () => {
        // Clear the state
        setGivenName("");
      });
    } else {
      console.log("Facebook SDK not loaded yet");
    }

    return () => {
      if (window.FB) {
        window.FB.Event.unsubscribe(
          "auth.statusChange",
          window.statusChangeCallback
        );
        window.FB.Event.unsubscribe("auth.logout", () => {});
      }
    };
  }, []);

  const handleFBLogin = () => {
    window.FB.login((response) => {
      window.statusChangeCallback(response);
    });
  };

  return (
    <div>
      <form onSubmit={(event) => event.preventDefault()}>
        <label htmlFor="email">Email</label>
        <input
          value={email}
          onChange={(event) => setEmail(event.target.value)}
        ></input>
      </form>
      <div
        className="fb-login-button"
        data-width=""
        data-size="large"
        data-button-type="continue_with"
        data-layout="default"
        data-auto-logout-link="false"
        data-use-continue-as="true"
        onClick={handleFBLogin}
      ></div>
      <div id="status"></div>
    </div>
  );
};

export default User_login;
