        },

        newPasswordRequire: (data) => {
          console.log("newPasswordRequire: ", data);
          resolve(data);
        },
      });
    });
  };
  return (
    <AccountContext.Provider value={{ authenticate }}>
      {props.children}
    </AccountContext.Provider>
  );
};

export default { Account, AccountContext };
