import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AccountContext } from "./User_Account.jsx";

const User_login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();
  const { authenticate, setAuthenticated } = useContext(AccountContext);

  useEffect(() => {
    // Load the Facebook SDK
    window.fbAsyncInit = function () {
      window.FB.init({
        appId: "730655258288890", // Replace with your Facebook App ID
        cookie: true,
        xfbml: true,
        version: "v15.0", // Replace with your Facebook SDK version
      });

      // Subscribe to 'auth.statusChange' event
      window.FB.Event.subscribe("auth.statusChange", function (response) {
        if (response.authResponse) {
          window.FB.api("/me", function (response) {
            authenticate(response.email, response.id)
              .then((data) => {
                console.log("Logged", data);
                setAuthenticated(true);
                navigate("/User_dashboard");
              })
              .catch((err) => {
                console.log("Failed to login", err);
              });
          });
        } else {
          console.log("User cancelled login or did not fully authorize.");
        }
      });
    };

    (function (d, s, id) {
      var js,
        fjs = d.getElementsByTagName(s)[0];
      if (d.getElementById(id)) return;
      js = d.createElement(s);
      js.id = id;
      js.src = "https://connect.facebook.net/en_US/sdk.js";
      fjs.parentNode.insertBefore(js, fjs);
    })(document, "script", "facebook-jssdk");
  }, []);

  const onSubmit = (event) => {
    event.preventDefault();

    authenticate(email, password)
      .then((data) => {
        console.log("Logged", data);
        setAuthenticated(true);
        navigate("/User_dashboard");
      })
      .catch((err) => {
        console.log("Failed to login", err);
      });
  };

  const onClickFacebookLogin = () => {
    window.FB.login(
      function (response) {
        if (response.authResponse) {
          window.FB.api("/me", function (response) {
            authenticate(response.email, response.id)
              .then((data) => {
                console.log("Logged", data);
                setAuthenticated(true);
                navigate("/User_dashboard");
              })
              .catch((err) => {
                console.log("Failed to login", err);
              });
          });
        } else {
          console.log("User cancelled login or did not fully authorize.");
        }
      },
      { scope: "email" }
    );
  };

  return (
    <div>
      <div
        className="fb-login-button"
        data-width=""
        data-size="large"
        data-button-type="continue_with"
        data-layout="default"
        data-auto-logout-link="false"
        data-use-continue-as="true"
      ></div>
      <div
        className="fb-signup-button"
        data-width=""
        data-size="large"
        data-button-type="sign_up_with"
        data-layout="default"
        data-auto-logout-link="false"
        data-use-continue-as="true"
      ></div>
    </div>
  );
};

export default User_login;

<form onSubmit={onSubmit}>
  <label htmlFor="email">Email</label>
  <input
    value={email}
    onChange={(event) => setEmail(event.target.value)}
  ></input>
  <label htmlFor="password">Password</label>
  <input
    value={password}
    onChange={(event) => setPassword(event.target.value)}
  ></input>
  <button type="submit">Login</button>
</form>;
