import React, { useState } from "react";
import Dropzone from "react-dropzone";
import { S3Client, PutObjectCommand } from "@aws-sdk/client-s3";
import { BsCloudUpload } from "react-icons/bs"; // Import the desired icon

// Configure the AWS SDK
const s3 = new S3Client({
  region: "us-east-1", // Replace with your desired region
  credentials: {
    accessKeyId: "AKIA2AJRN6P67CIV6TTD",
    secretAccessKey: "ZA/r6XSUs2GdDq5Io3ryjJolRvp31WMvIMKj9pRp",
  },
});

const ImageUpload = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [cost, setCost] = useState("");
  const [address, setAddress] = useState("");
  const [email, setEmail] = useState("");

  const handleFileDrop = (acceptedFiles) => {
    setSelectedFile(acceptedFiles[0]);
  };

  const handleUpload = async () => {
    if (selectedFile) {
      const params = {
        Bucket: "YOUR_BUCKET_NAME",
        Key: selectedFile.name,
        Body: selectedFile,
        ACL: "public-read", // Set the desired access control level for the uploaded image
      };

      try {
        const data = await s3.send(new PutObjectCommand(params));
        console.log("Image uploaded successfully:", data);
        // Perform any further actions after successful image upload
      } catch (err) {
        console.log("Error:", err);
      }
    }
  };

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100vh",
      }}
    >
      <div>
        <Dropzone onDrop={handleFileDrop}>
          {({ getRootProps, getInputProps }) => (
            <div {...getRootProps()}>
              <input {...getInputProps()} />
              {selectedFile ? (
                <div>Selected file: {selectedFile.name}</div>
              ) : (
                <div>
                  <h1>Create a new listing</h1>
                  <BsCloudUpload size={48} /> {/* Render the icon */}
                  <div>Drag and drop an image here or click to select</div>
                </div>
              )}
            </div>
          )}
        </Dropzone>
        <form>
          <label>
            Cost:
            <input
              type="text"
              value={cost}
              onChange={(e) => setCost(e.target.value)}
            />
          </label>
          <label>
            Address:
            <input
              type="text"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
            />
          </label>
          <label>
            Email:
            <input
              type="text"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </label>
        </form>
        <button onClick={handleUpload}>Upload</button>
      </div>
    </div>
  );
};

export default ImageUpload;
