import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { Auth } from "aws-amplify";
import { AccountContext } from "./User_Account.jsx";
import { FacebookLogin } from "facebook-login";

const User_login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();
  const { authenticate, setAuthenticated } = useContext(AccountContext);

  const statusChangeCallback = (response) => {
    console.log("Status Change Callback Response:", response);
    if (response.status === "connected") {
      getUserInfo();
    } else {
      console.log("User cancelled login or did not fully authorize.");
      document.getElementById("status").innerHTML =
        "Please log into this webpage.";
    }
  };

  const getUserInfo = () => {
    console.log("Getting user info");
    window.FB.api(
      "/me",
      {
        fields: "first_name,last_name,email",
        appsecret_proof: process.env.PASS_PHRASE,
      },
      async function (response) {
        console.log("User info response:", response);
        console.log("Successful login for: " + response.name);

        const authResponse = window.FB.getAuthResponse();
        let { accessToken, expiresIn } = authResponse;

        if (!accessToken || !expiresIn) {
          console.log("Access token or expiration time is missing.");
          return;
        }

        document.getElementById("status").innerHTML =
          "Thanks for logging in, " + response.name + "!";

        console.log("Facebook Access Token: ", accessToken);

        const { email, first_name, last_name } = response;

        console.log(
          "FACEBOOK_APP_SECRET: ",
          process.env.REACT_APP_FACEBOOK_APP_SECRET
        );

        try {
          const user = await Auth.currentAuthenticatedUser();
          // Update the user's profile information in the Amplify Auth service
          Auth.updateUserAttributes(
            user,
            {
              email,
              name: `${first_name} ${last_name}`,
            },
            (err, data) => {
              if (err) {
                console.log("Error updating user attributes:", err);
              } else {
                console.log("Successfully updated user attributes:", data);
              }
            }
          );
        } catch (error) {
          console.error("Error fetching the authenticated user: ", error);
        }

        // Redirect the user to the dashboard
        navigate("/User_dashboard");
      }
    );
  };

  const responseFacebook = (response) => {
    if (response.status !== "unknown") statusChangeCallback(response);
  };

  const onSubmit = async (event) => {
    event.preventDefault();

    // Authenticate the user with the Amplify Auth service
    try {
      const user = await Auth.signIn(email, password);
      console.log("Successfully signed in user:", user);
    } catch (error) {
      console.error("Error signing in user: ", error);
    }
  };

  return (
    <div>
      <form onSubmit={onSubmit}>
        <input value={email} onChange={(e) => setEmail(e.target.value)} />
        <input
          value={password}
          type="password"
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Sign In</button>
      </form>
      <FacebookLogin
        appId="730655258288890" // replace with your actual Facebook App ID
        autoLoad={true}
        fields="name,email,picture"
        onClick={() => {}}
        callback={responseFacebook}
      />
    </div>
  );
};

export default User_login;
