/* global FB */

import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { Auth } from "aws-amplify";
import { AccountContext } from "./User_Account.jsx";

const User_login = () => {
  const [email, setEmail] = useState("");
  const navigate = useNavigate();
  const { authenticate, setAuthenticated } = useContext(AccountContext);
  const [givenName, setGivenName] = useState("");

  const fbUser = "";

  useEffect(() => {
    if (window.FB) {
      window.FB.init({
        appId: "730655258288890",
        cookie: true,
        xfbml: true,
        version: "v15.0",
      });

      window.FB.getLoginStatus((response) => {
        window.statusChangeCallback(response);
      });

      // Add a listener to the auth.statusChange event
      window.FB.Event.subscribe(
        "auth.statusChange",
        window.statusChangeCallback
      );

      // Add a listener to the auth.logout event
      window.FB.Event.subscribe("auth.logout", () => {
        // Clear the state
        setGivenName("");
      });

      // Code that calls the statusChangeCallback function
      window.statusChangeCallback({ status: "connected" });
    } else {
      console.log("Facebook SDK not loaded yet");
    }

    return () => {
      if (window.FB) {
        window.FB.Event.unsubscribe(
          "auth.statusChange",
          window.statusChangeCallback
        );
        window.FB.Event.unsubscribe("auth.logout", () => {});
      }
    };
  }, []);

  const handleFBLogin = () => {
    window.FB.login((response) => {
      window.statusChangeCallback(response);
      testAPI();
    });
  };

  return (
    <div>
      <form onSubmit={(event) => event.preventDefault()}>
        <label htmlFor="email">Email</label>
        <input
          value={email}
          onChange={(event) => setEmail(event.target.value)}
        ></input>
      </form>
      <div
        className="fb-login-button"
        data-width=""
        data-size="large"
        data-button-type="continue_with"
        data-layout="default"
        data-auto-logout-link="false"
        data-use-continue-as="true"
        onClick={handleFBLogin}
      ></div>
      <div id="status"></div>
    </div>
  );
};

export default User_login;
