import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AccountContext } from "./User_Account.jsx";
import { Auth } from "aws-amplify";
import FacebookLogin from "facebook-login";

const User_Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  const facebookLogin = new FacebookLogin({
    appId: "730655258288890",
    scope: "public_profile",
    callback: async (response) => {
      if (response.status === "connected") {
        // The user is logged in.
        const { email, name } = await response.authResponse;
        console.log("User is logged in:", name);
        setName(name);
        setIsLoggedIn(true);
        navigate("/User_dashboard");
      } else {
        // The user is not logged in.
      }
    },
  });

  useEffect(() => {
    Auth.currentUserAsync().then((user) => {
      if (user) {
        setEmail(user.email);
        setIsLoggedIn(true);
      }
    });
  }, []);

  return (
    <div>
      <form onSubmit={onSubmit}>
        <label htmlFor="email">Email</label>
        <input
          value={email}
          onChange={(event) => setEmail(event.target.value)}
        ></input>
        <label htmlFor="password">Password</label>
        <input
          value={password}
          onChange={(event) => setPassword(event.target.value)}
        ></input>
        <button type="submit">Login</button>
      </form>
      <div
        className="fb-login-button"
        data-width=""
        data-size="large"
        data-button-type="continue_with"
        data-layout="default"
        data-auto-logout-link="false"
        data-use-continue-as="true"
      ></div>
      {isLoggedIn && <h1>Welcome, {name}</h1>}
    </div>
  );
};

export default User_Login;
