import React, { useState, useEffect } from "react";
import { LoginSocialFacebook } from "reactjs-social-login";
import { FacebookLoginButton } from "react-social-login-buttons";

function FacebookLogin() {
  const [profile, setProfile] = useState(null);

  useEffect(() => {
    const appId = "730655258288890";
    const onResolve = (response) => {
      setProfile(response.data);
    };
    const onReject = (error) => {
      console.log(error);
    };

    // Here you may want to call onResolve and onReject
    // but it's impossible without actual Facebook login
    // process, so I leave it as a comment

    // onResolve();
    // onReject();
  }, []);

  return (
    <div>
      <LoginSocialFacebook
        appId="730655258288890"
        onResolve={(response) => {
          console.log("Response: ", response);
          if (response && response.data) {
            setProfile(response.data);
          } else {
            console.error("Response or response.data is undefined");
          }
        }}
        onReject={(error) => {
          console.log(error);
        }}
      >
        <FacebookLoginButton />
      </LoginSocialFacebook>
      {profile ? (
        <div>
          <h1>{profile.name}</h1>
          <img src={profile.picture.data.url} alt="User" />
        </div>
      ) : (
        ""
      )}
    </div>
  );
}

export default FacebookLogin;
