import React, { useState } from "react";
import { LoginSocialFacebook } from "reactjs-social-login";
import { FacebookLoginButton } from "react-social-login-buttons";

const MyApp = () => {
  const [profile, setProfile] = useState(null);
  const [isPolling, setIsPolling] = useState(false);

  const poll = async () => {
    setIsPolling(true);
    try {
      const response = await fetch(`https://graph.facebook.com/me?fields=name`);
      const data = await response.json();
      setProfile(data);
      setIsPolling(false);
    } catch (error) {
      console.error(error);
    }
  };

  const onLogin = () => {
    setIsPolling(true);
    poll();
  };

  return (
    <div>
      <LoginSocialFacebook
        appId="730655258288890"
        onResolve={(response) => {
          setProfile(response.data);
        }}
        onReject={(error) => {
          console.log(error);
        }}
      >
        <FacebookLoginButton onClick={onLogin} />
      </LoginSocialFacebook>
      {profile ? (
        <div>
          <h1>{profile.name}</h1>
        </div>
      ) : (
        ""
      )}
      {isPolling ? (
        <div>Polling...</div>
      ) : (
        <div>
          <button onClick={poll}>Poll</button>
        </div>
      )}
    </div>
  );
};

export default MyApp;
